import "./css/App.css"
import {useState} from "react"
import {BrowserRouter as Router, Switch, Route, Link, useHistory} from "react-router-dom"

function App() {

  const history = useHistory();

  const [noOfQuestions, setNoOfQuestions] = useState(3)

  const questionsList = [
    "What is the speed of the light in water?",
    "What is the speed of the light in glass?",
    "What is the speed of the light in air?"
  ]
  var ar = []
  var j = 1
  for(var i=0; i<noOfQuestions; i++){
    ;
    ar[i] = j;
    j++;
  }

  function handleClick(i){

   console.log(questionsList[i])
   history.push({pathname: '/questionpalette',
                state: {
                  question: questionsList[i]
                }
                })
  }
  const questionNavigator = ar.map((q, i)=>
    <div onClick={()=>handleClick(i)} className="questionNoButton">{q}</div>
  )

  return (
    <Router>
    <div className="App container-fluid">
    <div className="row header-section">
      <div className="col-lg-3 header-left-col">
        logo 1
        </div>
        <div className="col-lg-6 header-middle-col">
          Quiz App
        </div>
        <div className="col-lg-3 header-right-col">
          logo 2
        </div>
         </div>
         <div className="row sub-header">
        <div className="col-lg-8 questionsNoList">
          <div>
            {questionNavigator}
          </div>
        </div>
        <div className="col-lg-4">
        </div>
         </div>
         <div className="row middle-section">
           <Switch>
             <Route exact to="/">
           <GreetUser />
             </Route>

          <Route to="/questionpalette">
            <QuestionPalette />
          </Route>
           </Switch>
         </div>
    </div>
    </Router>
  );
}
function QuestionPalette(){
  const ques = history.location.state.question;
  return(
    <div>
      <div className="question-statement">{ques}</div>
      <div className="options">
        <ul>
        <li>option 1</li>
        <li>option 2</li>
        <li>option 3</li>
        <li>option 4</li>
      </ul>
      </div>

    </div>
  )
}

function GreetUser(){
  return(
    <div>Hello, User</div>
  )
}

export default App;
