import "./css/App.css"
import {useState} from "react"
import React from "react"
import {Switch, Route,  Link, useHistory} from "react-router-dom"

function App() {

  const history = useHistory();

  var questionsList = {
   0: {
      ques: "What is the speed of the light in air?",
      option1: "A",
      option2: "B",
      option3: "C",
      option4: "D" 
    },
   1: {ques: "What is the speed of the light in water?",
   option1: "E",
   option2: "F",
   option3: "G",
   option4: "H" 
  },
   2: {ques:"What is the speed of the light in glass?",
   option1: "I",
   option2: "J",
   option3: "K",
   option4: "L" ,
},
3: {ques: "Who was the founder of Apple?",
option1: "Bill Gates",
option2: "Jack Ma",
option3: "Steve Jobs",
option4: "Sundar Pichai"

}
  }
  const [quesNo, setQuesNo] =useState(1);
  const [noOfQuestions, setNoOfQuestions] = useState(4)

  var ar = []
  var j = 1
  for(var i=0; i<noOfQuestions; i++){

    ar[i] = j;
    j++;
  }

  function handleClick(index){
    setQuesNo(index+1)

   history.push({
                pathname: '/questionpalette',
                state: {
                  quesNum: index+1,
                  question: questionsList[index].ques,
                  option1: questionsList[index].option1,
                  option2: questionsList[index].option2,
                  option3: questionsList[index].option3,
                  option4: questionsList[index].option4

                }
                })

  }
function handlePrevClick(){
  console.log(quesNo)
 if(quesNo>1){

  history.push({
    pathname: '/questionpalette',
    state: {
      quesNum: quesNo-1,
      question: questionsList[quesNo-2].ques,
      option1: questionsList[quesNo-2].option1,
      option2: questionsList[quesNo-2].option2,
      option3: questionsList[quesNo-2].option3,
      option4: questionsList[quesNo-2].option4
    }
    })
 }
}
function handleNextClick(){
  console.log(quesNo)
if(quesNo<=noOfQuestions){
  history.push({
    pathname: '/questionpalette',
    state: {
      quesNum: quesNo+1,
      question: questionsList[quesNo].ques,
      option1: questionsList[quesNo].option1,
      option2: questionsList[quesNo].option2,
      option3: questionsList[quesNo].option3,
      option4: questionsList[quesNo].option4
    }
    })
}

}
  const questionNavigator = ar.map((q, index)=>
   <div><button onClick={()=>handleClick(index)} className="questionNoButton">{q}</button></div>
  )

  return (
    <div className="App-screen container-fluid">
    <div className="row header-section">
      <div className="col-md-3 header-left-col">
        logo 1
        </div>
        <div className="col-md-6 header-middle-col">
          Quiz App
        </div>
        <div className="col-md-3 header-right-col">
          logo 2
        </div>
         </div>
         <div className="row sub-header">
        <div className="col-md-12 questionsNoList">
            {questionNavigator}
        </div>
         </div>
         <div className="row middle-section">
           <Switch>   
           <Route exact path="/">
             <GreetUser />
           </Route> 
          <Route path="/questionpalette">
            <QuestionTemplate />
          </Route>
           </Switch>
         </div>
         <div className="row navigation-buttons">
           <div className="col-md-2"></div>
   <div className="col-md-2"><button onClick={handlePrevClick}>Prev</button></div>
   <div className="col-md-2"><button onClick={handleNextClick}>Next</button></div>
   <div className="col-md-1"></div>
   <div className="col-md-2"><button>Abort</button></div>
   <div className="col-md-2"><button>Submit</button></div>

         </div>
    </div>
  );
}
function QuestionTemplate(){
  const history = useHistory(); 
var quesNum = history.location.state.quesNum
  return(
    <div className="row question-template">
      <div className="row question-statement">{history.location.state.question}</div>
      <div className="row options">
        <div className="col-md-4"></div>
      <div className="col-md-8"> 
       <ul>
        <li>{history.location.state.option1}</li>
        <li>{history.location.state.option2}</li>
        <li>{history.location.state.option3}</li>
        <li>{history.location.state.option4}</li>
      </ul>
      </div>
    </div>
    </div>
  )
}

function GreetUser(){
  return(
    <div>Hello, User</div>
  )
}

export default App;
