import "./css/App.css"
import {useState} from "react"
import React from "react"
import {Switch, Route, useHistory} from "react-router-dom"

function App() {

  const history = useHistory();
  console.log(history)

  const [noOfQuestions, setNoOfQuestions] = useState(3)


  const questionsList = {
    0: "What is the speed of the light in air?",
    1: "What is the speed of the light in water?",
    2: "What is the speed of the light in glass?"
  }
  const [question, setQuestion] = useState(questionsList[0])
  var ar = []
  var j = 1
  for(var i=0; i<noOfQuestions; i++){
    ;
    ar[i] = j;
    j++;
  }

  function handleClick(index){
  console.log(questionsList[index])
  setQuestion(questionsList[index])
   history.push({
                pathname: '/questionpalette',
                state: {
                  question: question
                }
                })
                console.log(history)
                console.log(index)
  }

  const questionNavigator = ar.map((q, index)=>
    <div onClick={()=>handleClick(index)} className="questionNoButton">{q}</div>
  )

  return (
    <div className="App container-fluid">
    <div className="row header-section">
      <div className="col-md-3 header-left-col">
        logo 1
        </div>
        <div className="col-md-6 header-middle-col">
          Quiz App
        </div>
        <div className="col-md-3 header-right-col">
          logo 2
        </div>
         </div>
         <div className="row sub-header">
        <div className="col-lg-8 questionsNoList">
          <div>
            {questionNavigator}
          </div>
        </div>
        <div className="col-md-4">
        </div>
         </div>
         <div className="row middle-section">
           <Switch>    
          <Route to="/questionpalette">
            <QuestionPalette />
          </Route>
           </Switch>
         </div>
    </div>
  );
}
function QuestionPalette(){
  const history = useHistory();
  const ques = history.location.state.question
  return(
    <div>
      <div className="question-statement">{ques}</div>
      <div className="options">
        <ul>
        <li>option 1</li>
        <li>option 2</li>
        <li>option 3</li>
        <li>option 4</li>
      </ul>
      </div>

    </div>
  )
}

function GreetUser(){
  return(
    <div>Hello, User</div>
  )
}

export default App;
