import "./css/App.css"
import {useState} from "react"
import React from "react"
import {Switch, Route,  Link, useHistory} from "react-router-dom"

function App() {

  const history = useHistory();

  const [ques, setQues] = useState("questionsList[0]")
  const [noOfQuestions, setNoOfQuestions] = useState(3)

  var ar = []
  var j = 1
  for(var i=0; i<noOfQuestions; i++){

    ar[i] = j;
    j++;
  }

  function handleClick(index){
  // console.log(questionsList[index].ques)
  // setQues(questionsList[index].ques)
   history.push({
                pathname: '/questionpalette',
                state: {
                  k : index
                  // question: questionsList[index].ques,
                  // option1: questionsList[index].option1,
                  // option2: questionsList[index].option2,
                  // option3: questionsList[index].option3,
                  // option4: questionsList[index].option4

                }
                })
                console.log(history)
                console.log(index)
  }

  const questionNavigator = ar.map((q, index)=>
    <div onClick={()=>handleClick(index)} className="questionNoButton">{q}</div>
  )

  return (
    <div className="App container-fluid">
    <div className="row header-section">
      <div className="col-md-3 header-left-col">
        logo 1
        </div>
        <div className="col-md-6 header-middle-col">
          Quiz App
        </div>
        <div className="col-md-3 header-right-col">
          logo 2
        </div>
         </div>
         <div className="row sub-header">
        <div className="col-lg-8 questionsNoList">
          <div>
            {questionNavigator}
          </div>
        </div>
        <div className="col-md-4">
        </div>
         </div>
         <div className="row middle-section">

           <Switch>   
           <Route exact path="/">
             <GreetUser />
           </Route> 
          <Route path="/questionpalette">
            <QuestionPalette />
          </Route>
           </Switch>
         </div>
    </div>
  );
}
function QuestionPalette(){

  const history = useHistory(); 

  var questionsList = {
    0: {
       ques: "What is the speed of the light in air?",
       option1: "A",
       option2: "B",
       option3: "C",
       option4: "D" 
     },
    1: {ques: "What is the speed of the light in water?",
    option1: "E",
    option2: "F",
    option3: "G",
    option4: "H" 
   },
    2: {ques:"What is the speed of the light in glass?",
    option1: "I",
    option2: "J",
    option3: "K",
    option4: "L" 
 }
   }

  var i = history.location.state.k
  return(
    <div>
     <Link to="/"> <button>Back</button> </Link>
      <div className="question-statement">{history.location.state.question}</div>
      <div className="options">
        <ul>
        <li>{questionsList[i].option1}</li>
        <li>{questionsList[i].option2}</li>
        <li> {questionsList[i].option3}</li>
        <li> {questionsList[i].option4}</li>
      </ul>
      </div>

    </div>
  )
}

function GreetUser(){
  return(
    <div>Hello, User</div>
  )
}

export default App;
