  )

  return (
    <Router>
    <div className="App container-fluid">
    <div className="row header-section">
      <div className="col-lg-3 header-left-col">
        logo 1
        </div>
        <div className="col-lg-6 header-middle-col">
          Quiz App
        </div>
        <div className="col-lg-3 header-right-col">
          logo 2
        </div>
         </div>
