import "./css/App.css"
import {useState} from "react"
import React from "react"
import {Switch, Route,  Link, useHistory} from "react-router-dom"

function App() {

  const history = useHistory();
  console.log(history)

  var questionsList = {
   0: {
      ques: "What is the speed of the light in air?",
      option1: "A",
      option2: "B",
      option3: "C",
      option4: "D" 
    },
   1: {ques: "What is the speed of the light in water?",
   option1: "E",
   option2: "F",
   option3: "G",
   option4: "H" 
  },
   2: {ques:"What is the speed of the light in glass?",
   option1: "I",
   option2: "J",
   option3: "K",
   option4: "L" 
}
  }

  const [ques, setQues] = useState(questionsList[0])
  const [quesNo, setQuesNo] =useState(1);
  const [index, setIndex] = useState(0)
  const [noOfQuestions, setNoOfQuestions] = useState(3)

  var ar = []
  var j = 1
  for(var i=0; i<noOfQuestions; i++){

    ar[i] = j;
    j++;
  }

  function handleClick(i){
    setQuesNo(i+1)
    setIndex(i)
  console.log(questionsList[i].ques)
  setQues(questionsList[i].ques)
   history.push({
                pathname: '/questionpalette',
                state: {
                  quesNum: index+1,
                  question: questionsList[i].ques,
                  option1: questionsList[i].option1,
                  option2: questionsList[i].option2,
                  option3: questionsList[i].option3,
                  option4: questionsList[i].option4

                }
                })
                console.log(history)
                console.log(index)
  }
  const questionNavigator = ar.map((q, i)=>
   <div> <button onClick={()=>handleClick(i)} className="questionNoButton">{q}</button></div>
  )

  return (
    <div className="App-screen container-fluid">
    <div className="row header-section">
      <div className="col-md-3 header-left-col">
        logo 1
        </div>
        <div className="col-md-6 header-middle-col">
          Quiz App
        </div>
        <div className="col-md-3 header-right-col">
          logo 2
        </div>
         </div>
         <div className="row sub-header">
        <div className="col-md-12 questionsNoList">
            {questionNavigator}
        </div>
         </div>
         <div className="row middle-section">
           <Switch>   
           <Route exact path="/">
             <GreetUser />
           </Route> 
          <Route path="/questionpalette">
            <QuestionTemplate />
          </Route>
           </Switch>
         </div>
         <div className="row navigation-buttons">
           <div className="col-md-2"></div>
   <div className="col-md-2"><button onClick={()=>handleClick(index-1<0?index: index-1)}>Prev</button></div>
   <div className="col-md-2"><button>Next</button></div>
   <div className="col-md-1"></div>
   <div className="col-md-2"><button>Abort</button></div>
   <div className="col-md-2"><button>Submit</button></div>

         </div>
    </div>
  );
}
function QuestionTemplate(){
  const history = useHistory(); 
var quesNum = history.location.state.quesNum
  return(
    <div className="row question-template">
      <div className="row question-statement">{history.location.state.question}</div>
      <div className="row options">
        <div className="col-md-4"></div>
      <div className="col-md-8"> 
       <ul>
        <li>{history.location.state.option1}</li>
        <li>{history.location.state.option2}</li>
        <li>{history.location.state.option3}</li>
        <li>{history.location.state.option4}</li>
      </ul>
      </div>
    </div>
    </div>
  )
}

function GreetUser(){
  return(
    <div>Hello, User</div>
  )
}

export default App;
