import "./css/App.css"
import {useState} from "react"
import {BsCircle} from "react-icons/bs"

function App() {

  const [noOfQuestions, setNoOfQuestions] = useState(9)

  var ar = []
  var j = 1
  for(var i=0; i<noOfQuestions; i++){
    ;
    ar[i] = j;
    j++;
  }

  const questionNavigator = ar.map((q)=>
    <div className="questionNoButton">{q}</div>
  )

  return (
    <div className="App container-fluid">
    <div className="row header-section">
      <div className="col-lg-3 header-left-col">
        logo 1
        </div>
        <div className="col-lg-6 header-middle-col">
          Quiz App
        </div>
        <div className="col-lg-3 header-right-col">
          logo 2
        </div>
         </div>
         <div className="row sub-header">
        <div className="col-lg-8 questionsNoList">
          <div>
            {questionNavigator}
          </div>
        </div>
        <div className="col-lg-4">
        </div>
         </div>
         <div className="row middle-section">
           <QuestionPalette />
         </div>
    </div>
  );
}
function QuestionPalette(){
  const ques = "What is the speed of light in glass?";
  return(
    <div>
      <div>{ques}</div>
      <ul>
        <li>option 1</li>
        <li>option 2</li>
        <li>option 3</li>
        <li>option 4</li>
      </ul>
    </div>
  )
}

export default App;
